(function() {
  var addClick, addEvent, elModal;

  window.editor = ace.edit("editor");

  addEvent = function(ev, id, fn) {
    return document.getElementById(id).addEventListener(ev, fn);
  };

  addClick = function(id, fn) {
    return addEvent("click", id, fn);
  };

  elModal = document.getElementById("modal");

  window.addEventListener("click", function(ev) {
    if (ev.target === elModal) {
      return elModal.style.display = "none";
    }
  });

  addClick("button--new", function() {
    return editor.setValue("");
  });

  addClick("button--save", function() {
    return elModal.style.display = "block";
  });

  editor.getSession().selection.on("changeCursor", function() {
    var cursor;
    cursor = editor.selection.getCursor();
    return document.getElementById("editor--statusbar--linecol").innerText = "Ln " + (cursor.row + 1) + ", Col " + (cursor.column + 1);
  });

}).call(this);

